import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "./Navbar";
const Leaderboard = () => {
  const [leaderboardData, setLeaderboardData] = useState([]);

  useEffect(() => {
    axios
    .get(`http://localhost:3000/leaderboard`)
    .then((response) => {
      setLeaderboardData(response.data);
    })
    .catch((error) => {
      console.error("Error fetching user data:", error);
    });
  }, []);

  console.log(leaderboardData);

  return (
    <div className="bg-gray-800 min-h-screen">
      <Navbar />
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-semibold text-white mb-6">Leaderboard</h1>
        <div className="mb-4 flex space-x-4">
          <input
            type="text"
            placeholder="Search username"
            //value={searchTerm}
            //onChange={(e) => setSearchTerm(e.target.value)}
            className="w-1/4 px-3 py-2 rounded bg-gray-700 text-white"
          />
          <button ></button>
         
        </div>
        <table className="w-full table-fixed">
        <thead>
  <tr>
    <th
      className="text-white w-1/4 py-2 cursor-pointer"
      //onClick={() => handleSort("username")}
    >
      Username
    </th>
    <th
      className="text-white w-1/4 py-2 cursor-pointer"
      //onClick={() => handleSort("score")}
    >
      Score
    </th>
    <th
      className="text-white w-1/4 py-2 cursor-pointer"
      //onClick={() => handleSort("average_score")}
    >
      Average Score
    </th>
    <th
      className="text-white w-1/4 py-2 cursor-pointer"
      //onClick={() => handleSort("number_of_games")}
    >
      Number of Games
    </th>
  </tr>
</thead>
          <tbody>
            {leaderboardData.map((item, index) => (
              <tr key={index}>
                <td className="text-white py-2">{item.username}</td>
                <td className="text-white py-2">{item.score}</td>
                <td className="text-white py-2">{item.average_score}</td>
                <td className="text-white py-2">{item.number_of_games}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Leaderboard;
