import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Navbar from "./Navbar";
const Profile = () => {
  const token = sessionStorage.getItem("token");
  if (!token) {
    window.location.href = "/";
  }

  const handleLogout = () => {
    sessionStorage.removeItem("token");
    window.location.reload();
  };

  const { id } = useParams(); // Get the user's ID from the URL
  const [userData, setUserData] = useState({});

  useEffect(() => {
    // Fetch user data based on the ID
    axios.get(`http://localhost:3000/profile/${id}`)
      .then(response => {
        setUserData(response.data);
      })
      .catch(error => {
        console.error("Error fetching user data:", error);
      });
  }, [id]);

   

  return (
   <div className="bg-gray-800 min-h-screen">
  <Navbar />
  <div className="h-screen flex flex-col justify-center items-center bg-gray-900 text-white">
    <h1 className="text-7xl mb-6">{userData.username}'s stats</h1>

    {/* Table to display user statistics */}
    <table className="text-2xl w-2/3 border-collapse">
      {/* ... Table code ... */}
    </table>

    <div className="mt-8">
      {/* Change Password Section */}
      <div className="mb-4">
        <h2 className="text-3xl mb-2">Change Password</h2>
        <form>
          <div className="mb-2">
            <label htmlFor="currentPassword" className="block text-xl">
              Current Password:
            </label>
            <input
              type="password"
              id="currentPassword"
              className="w-full px-3 py-2 rounded border"
              placeholder="Enter current password"
            />
          </div>
          <div className="mb-2">
            <label htmlFor="newPassword" className="block text-xl">
              New Password:
            </label>
            <input
              type="password"
              id="newPassword"
              className="w-full px-3 py-2 rounded border"
              placeholder="Enter new password"
            />
          </div>
          <div>
            <button
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Change Password
            </button>
          </div>
        </form>
      </div>

      {/* Update Username Section */}
      <div className="mb-4">
        <h2 className="text-3xl mb-2">Update Username</h2>
        <form>
          <div className="mb-2">
            <label htmlFor="newUsername" className="block text-xl">
              New Username:
            </label>
            <input
              type="text"
              id="newUsername"
              className="w-full px-3 py-2 rounded border"
              placeholder="Enter new username"
            />
          </div>
          <div>
            <button
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Update Username
            </button>
          </div>
        </form>
      </div>
    </div>

    <button
      className="mt-4 px-6 py-3 bg-red-600 hover:bg-red-700 text-white rounded"
      onClick={handleLogout}
    >
      Logout
    </button>
  </div>
</div>


  );
};

export default Profile;
