import React from "react";
import jwt_decode from "jwt-decode";
const Profile = () => {

  const token = sessionStorage.getItem("token");
  if(!token){
    window.location.href = "/";
  }

  const handleLogout = () => {
    sessionStorage.removeItem("token");
    window.location.reload();
  };

  const userStats = {
    score: 500,
    numberOfGames: 25,
    averageScore: 20,
  };

  const decodedToken = jwt_decode(token);
  const username = decodedToken.id;
  return(
    <div className="h-screen flex flex-col justify-center items-center bg-gray-900 text-white">
      <h1 className="text-4xl mb-6">Profile Page</h1>
      
      {/* Table to display user statistics */}
      <table className="w-2/3 border-collapse border border-gray-600">
        <thead>
          <tr>
            <th className="border border-gray-600 p-2">Score</th>
            <th className="border border-gray-600 p-2">Number of Games</th>
            <th className="border border-gray-600 p-2">Average Score</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td className="border border-gray-600 p-2">{userStats.score}</td>
            <td className="border border-gray-600 p-2">{userStats.numberOfGames}</td>
            <td className="border border-gray-600 p-2">{userStats.averageScore}</td>
          </tr>
        </tbody>
      </table>
      
      <button
        className="mt-4 px-6 py-3 bg-red-600 hover:bg-red-700 text-white rounded"
        onClick={handleLogout}
      >
        Logout
      </button>
    </div>
  );
};

export default Profile;
