import React, { useRef } from 'react';
import axios from "axios";
import { useEffect, useState } from "react";



// SkockoGuessingGame component declaration at the top level
const SkockoGuessingGame = () => {
  
    const [currentGuessRow, setCurrentGuessRow] = useState(1);
    const [currentGuessCol, setCurrentGuessCol] = useState(1);
    const [numberOfGuess, setNumberOfGuess] = useState(1);
    const niz = ["1", "1", "1", "1"];
    const [guessNiz, setGuessNiz] = useState([]);
    const [hitArray, setHitArray] = useState([]);
    const [feedbackRows, setFeedbackRows] = useState([]);
  
    const guess = () => {
      let correct = 0;
      let missplaced = 0;
      let wrong = 0;
      let guessCopy = [...guessNiz];
      let nizCopy = [...niz];
  
      for (let i = 0; i < guessCopy.length; i++) {
        if (guessCopy[i] === nizCopy[i]) {
          correct++;
          guessCopy.splice(i, 1);
          nizCopy.splice(i, 1);
          i--;
        }
      }
      for (let i = 0; i < guessCopy.length; i++) {
        if (nizCopy.includes(guessCopy[i])) {
          missplaced++;
        }
      }
      wrong = 4 - (correct + missplaced);
      console.log();
      console.log();
      console.log(correct + " - " + missplaced +" - " + wrong);
  
      // Update feedback rows state
      const feedbackRow = (
        <div key={`feedback-${numberOfGuess}`}>
          {[...Array(correct)].map((_, index) => (
            <div
              key={`circle-${index + 1}-row-${currentGuessRow}`}
              className="circle bg-red-600"
            ></div>
          ))}
          {[...Array(missplaced)].map((_, index) => (
            <div
              key={`circle-${index + correct + 1}-row-${currentGuessRow}`}
              className="circle bg-yellow-600"
            ></div>
          ))}
          {[...Array(wrong)].map((_, index) => (
            <div
              key={`circle-${index + correct + missplaced + 1}-row-${currentGuessRow}`}
              className="circle bg-black-600"
            ></div>
          ))}
        </div>
      );
  
      setFeedbackRows((prevFeedbackRows) => [...prevFeedbackRows, feedbackRow]);
  
      if (correct === 4) {
        alert("Congrats!");
        window.location.reload();
      }
  
      setNumberOfGuess((prev) => prev + 1);
      setCurrentGuessRow((prev) => prev + 1);
      setCurrentGuessCol(0);
      setGuessNiz([]);
    };


    useEffect(() => {
        console.log(guessNiz); // This will run every time guessNiz is updated
      }, [guessNiz]);
    const handleGuessSubmission = (value) => {
        const buttonValue = value;
        const currentGuessRowElement = document.getElementById(
          `guess-${currentGuessRow}`
        );

        if (currentGuessRowElement) {
          const emptySquare = currentGuessRowElement.querySelector(
            ".square:not(.filled)"
          );


    
          if (emptySquare) {
            emptySquare.innerHTML = buttonValue;
            setGuessNiz((prevGuessNiz) => [...prevGuessNiz, buttonValue]);
            emptySquare.classList.add("filled");
            
          }
    
          setCurrentGuessCol((prevCol) => prevCol + 1);
          if (currentGuessCol > 4) {
            if (numberOfGuess === currentGuessRow) {
              alert("ASdasdasd");
              return;
            }
            setCurrentGuessRow((prevRow) => prevRow + 1);
            setCurrentGuessCol(0);
          }
        }
      };
    

    const reset = () => {
        window.location.reload();
    };

  // Helper function to generate the guess row JSX
  const renderGuessRow = (rowId) => (
    
    <div className="flex space-x-4" >
         <div className="guess-row flex space-x-4"  id={`guess-${rowId}`}>
            {Array.from({ length: 4 }, (_, i) => (<div key={`guess-${i+1}`}  className="square w-16 h-16 rounded border border-black"></div> ))}
        </div>
        
        <div id={`feedback-${rowId}`} className="flex flex-col mt-4">
            <div className="feedback-row flex space-x-4">
            {Array.from({ length: 4 }, (_, i) => ( <div key={`feeback-${i}`}  id={`feedback-${i+1}`} className="circle w-8 h-8 rounded-full border border-black"></div>))}
        </div>
    </div>
    </div>
  );

  return (
    <div className="flex flex-col items-center">
    
    {Array.from({ length: 6 }, (_, i) => ( <div key={`guess-${i + 1}`} className="mb-4"> {renderGuessRow(i + 1)}</div>))}
      <div id="controls" className="flex justify-center mt-4">
        <button id="btn1" value="1" className="guess-btn px-2 py-2 bg-blue-600 hover:bg-blue-700 text-black rounded" onClick={() => handleGuessSubmission(1)}>1</button>
        <button id="btn2" value="2" className="guess-btn ml-4 px-4 py-2 bg-red-600 hover:bg-red-700 text-black rounded" onClick={() => handleGuessSubmission(2)}>2</button>
        <button id="btn3" value="3" className="guess-btn px-4 py-2 bg-blue-600 hover:bg-blue-700 text-black rounded" onClick={() => handleGuessSubmission(3)}>3</button>
        <button id="btn4" value="4" className="guess-btn ml-4 px-4 py-2 bg-red-600 hover:bg-red-700 text-black rounded" onClick={() => handleGuessSubmission(4)}>4</button>
        <button id="btn5" value="5" className="guess-btn px-4 py-2 bg-blue-600 hover:bg-blue-700 text-black rounded" onClick={() => handleGuessSubmission(5)}>5</button>
        <button id="btn6" value="6" className="guess-btn ml-4 px-4 py-2 bg-red-600 hover:bg-red-700 text-black rounded" onClick={() => handleGuessSubmission(6)}>6</button>
        
        <button className="guess-btn px-4 py-2 bg-blue-600 hover:bg-blue-700 text-black rounded" onClick={guess}>Submit Guess</button>
        <button className="ml-4 px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded" onClick={reset}>Reset Game</button>
    </div>

      <div id="score" className="mt-4">
        {/* Add the score display here */}
        {/* For example: */}
        {/* <div id="current-score">Score: 0</div> */}
      </div>
    </div>
  );
};

export default SkockoGuessingGame;
