import React from 'react';
import axios from "axios";
import { useEffect, useState } from "react";



// SkockoGuessingGame component declaration at the top level
const SkockoGuessingGame = () => {
  const [guessNiz, setGuessNiz] = useState([]);
  const [currentGuessRow, setCurrentGuessRow] = useState(1);

  const handleGuessSubmission = (event) => {
    // Your existing handleGuessSubmission logic goes here
  };

  const guess = () => {
    var correct = 0;
        var missplaced = 0;
        var wrong = 0;
        var guessCopy = guessNiz.slice();
        var nizCopy = niz.slice();
        let checkNiz = new Set();

        for (let i = 0; i < guessCopy.length; i++) {
          if (guessCopy[i] === nizCopy[i]) {
            correct++;
            guessCopy.splice(i, 1);
            nizCopy.splice(i, 1);
            i--;
          }
        }

        console.log("---------1123----------------");
        console.log(guessCopy);
        console.log(nizCopy);

        for (let i = 0; i < guessCopy.length; i++) {
          if (nizCopy.includes(guessCopy[i])) {
            missplaced++;
          }
        }

        console.log("-------------------------");
        console.log(guessCopy);
        console.log(nizCopy);
        wrong = 4 - (correct + missplaced);
        console.log(correct + " " + missplaced + " " + wrong);

        const feedback = document.getElementById(`feedback-${numberOfGuess}`);
        const circles = feedback.querySelectorAll(".circle");

        let counterC = 0;
        let counterM = 0;
        for (let i = 1; i < 5; i++) {
          let current = document.getElementById(
            `circle-${i}-row-${currentGuessRow}`
          );
          if (counterC < correct) {
            current.classList.add("bg-red-600");
            counterC++;
          } else if (counterM < missplaced) {
            current.classList.add("bg-yellow-600");
            counterM++;
          } else {
            current.classList.add("bg-black-600");
          }
        }

        if (correct == 4) {
          alert("Congrats!");
          window.location.reload();
        }

        numberOfGuess++;
        currentGuessRow++;
        currentGuessCol = 0;
        guessNiz = [];
  };

  const reset = () => {
    window.location.reload();
  };

  // Helper function to generate the guess row JSX
  const renderGuessRow = (rowId) => (
    
    <div className="flex space-x-4">
    {Array.from({ length: 4 }, (_, i) => (
      <div
        key={`guess-row-${rowId}-square-${i}`}
        className="square w-16 h-16 rounded border border-black"
      ></div>
    ))}
    <div
      id={`feedback-${rowId}`}
      className="flex flex-col mt-4"
    >
      <div className="feedback-row flex space-x-4">
        {Array.from({ length: 4 }, (_, i) => (
          <div
            key={`feedback-row-${rowId}-circle-${i}`}
            className="circle w-8 h-8 rounded-full border border-black"
          ></div>
        ))}
      </div>
      {/* ... and so on, total of 6 feedback rows */}
    </div>
  </div>
  );

  return (
    <div className="flex flex-col items-center">
    {/* Render the guess rows */}
    {Array.from({ length: 6 }, (_, i) => (
      <div key={`guess-${i + 1}`} className="mb-4">
        {renderGuessRow(i + 1)}
      </div>
    ))}
      <div id="controls" className="flex justify-center mt-4">
        {/* Add the game controls here, like buttons for submitting a guess, resetting the game, etc. */}
        {/* Button for submitting a guess */}
        <button
          className="guess-btn px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded"
          onClick={handleGuessSubmission}
        >
          Submit Guess
        </button>
        {/* Button for resetting the game */}
        <button
          className="ml-4 px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded"
          onClick={reset}
        >
          Reset Game
        </button>
        {/* ... and so on */}
      </div>

      <div id="result" className="mt-4">
        {/* Add the result display here, showing if the guess is correct or providing feedback */}
        {/* For example: */}
        {/* <div id="guess-result-1">Result for Guess 1</div>
             <div id="guess-result-2">Result for Guess 2</div>
             ... and so on */}
      </div>

      <div id="score" className="mt-4">
        {/* Add the score display here */}
        {/* For example: */}
        {/* <div id="current-score">Score: 0</div> */}
      </div>
    </div>
  );
};

export default SkockoGuessingGame;
